import React from "react";

import clsx from "clsx";
import Image from "next/image";
import img from "./assets/img/bottom.png";

import { twMerge } from "tailwind-merge";

function cn(...inputs) {
	return twMerge(clsx(...inputs));
}
const TaxiCounts = () => {
	const [swiper, setSwiper] = React.useState(null);
	const [activeCount, setActiveCount] = React.useState(1);

	return (
		<section className="book-ride-section py-[60px] overflow-hidden">
			<div className="container max-sm:px-6">
				<h2 className="text-[#0044FF] text-[30px] leading-[36px] font-bold">
					Aegean Taxi in Numbers
				</h2>
				<h5 className="text-xl font-bold text-[#737171] mb-12">
					Since 2015
				</h5>
			</div>
			<div className="container max-sm:px-0">
				<div className="grid grid-cols-3 gap-y-10">
					{data.map((item, index) => (
						<div
							className={cn("text-center", {
								"border-r border-black": index % 3 !== 2,
							})}
							key={index}
						>
							<div className="mb-[10px] flex justify-center">
								{item.icon}
							</div>
							<h5 className="text-xl font-bold mb-2">{item.count}</h5>
							<p
								className="text-sm font-semibold text-[#000] leading-[16px]"
								dangerouslySetInnerHTML={{
									__html: item.text,
								}}
							></p>
						</div>
					))}
				</div>
			</div>
			<div className="px-9">
				<div className="relative mt-12 flex justify-between">
					<button
						className="btn flex items-center gap-2 justify-center bg-[#004080] text-white h-9 rounded-full text-sm px-4 pr-10"
						type="button"
					>
						<span>Find out more about us</span>
					</button>
				</div>
			</div>
			<div className="px-2">
				<Image
					src={img}
					width={720}
					height={810}
					className="max-w-full object-contain"
					alt=""
				/>
			</div>
		</section>
	);
};

