const ExperienceFastestWay = () => {
	return (
		<section className="experience-fastest-ways-section py-[60px]">
			<div className="container px-6">
				<h2 className="text-[32px] leading-[1.25] font-bold mb-7">
					Experience the fastest way to book a ride online, with Aegean
					Taxi
				</h2>
				<br />
				<div className="experience-fastest-ways">
					{data.map((item, index) => (
						<div className="step" key={index}>
							<span className="icon">{item.icon}</span>
							<h6 className="subtitle">{item.title}</h6>
							<p className="text-sm font-medium">{item.subtitle}</p>
						</div>
					))}
				</div>
				<div className="flex flex-wrap gap-6 mt-7">
					<Link href="" className="block max-w-[calc(50%-12px)] w-[138px]">
						<Image
							className="w-full object-contain"
							src={appstore}
							width={336}
							height={80}
							alt=""
						/>
					</Link>
					<Link href="" className="block max-w-[calc(50%-12px)] w-[138px]">
						<Image
							className="w-full object-contain"
							src={whatsapp}
							width={336}
							height={80}
							alt=""
						/>
					</Link>
				</div>
			</div>
		</section>
	);
};
